printf "â‚¿ %s \t%.${VAR_DECIMALS}f EUR / %.${VAR_DECIMALS}f USD | color=green\n" "Buy price:" "$btc_buy_price_eur" "$btc_buy_price_usd"
printf "â‚¿ %s \t%.${VAR_DECIMALS}f EUR / %.${VAR_DECIMALS}f USD | color=green\n" "Sell price:" "$btc_sell_price_eur" "$btc_sell_price_usd"

